import {Redirect} from 'react-router-dom'
import axios from 'axios'
import {useState, useEffect} from 'react'
import swal from '@sweetalert/with-react'

const Detalle = () => {

 const token = localStorage.getItem('token')
 let query = new URLSearchParams(window.location.search) 
 let movie_id = query.get('id') 
 console.log(movie_id)

 const [movie, setMovie] = useState(null)

 useEffect(() => { 
   const endpoint = `https://api.themoviedb.org/3/movie/${movie_id}?api_key=c5256bd6913284c73d535a2dd158d799&language=es-ES` 

   axios 
    .get(endpoint) 
    .then(res => { 
      console.log(res.data) 
      const movieData = res.data 
      setMovie(movieData) 
    }) .catch(e => { swal('hubo errores al intentar encontrar los datos de la pelicula') })
 }, []) 
 return ( <> {!token && <Redirect to="/"/>} <div className="card"> <img className="card-img-top" src={`https://image.tmdb.org/t/p/w500/${movie.poster_path}`} alt="Card image cap"/> <div className="card-body"> <h5 className="card-title">{movie.title}</h5> <p className="card-text">{movie.overview}</p> <p className="card-text">Release date: {movie.release_date}</p> {movie.genres.map((el) => <li>{el.name}</li>)} </div> </div> </> )}
export default Detalle;
