import {Redirect} from 'react-router-dom'
import axios from 'axios'
import {useState, useEffect} from 'react'
import swal from '@sw'
const Detalle = () => {

  const token = localStorage.getItem('token')

  const query = new URLSearchParams(window.location.search)
  const movie_id = query.get('id')
  console.log(movie_id)

  const [detalle, setDetalle] = useState("")

  const endpoint = `https://api.themoviedb.org/3/movie/${movie_id}?api_key=c5256bd6913284c73d535a2dd158d799&language=es-ES`

  useEffect(() => {
    axios
    .get(endpoint)
    .then(res => {
      console.log(res.data)
      const resultado = res.data
      setDetalle(resultado)
    })
    .catch(e => {
      swal('hubo errores al intentar encontrar los datos de la pelicula')
    })

  }, [detalle])


  return (
    <>
    {!token && <Redirect to="/"/>}

    </>
  )
}

export default Detalle
