import {Redirect} from 'react-router-dom'
import axios from 'axios'
import {useState, useEffect} from 'react'
import swal from '@sweetalert/with-react'

const Detalle = () => {

 const token = localStorage.getItem('token')
 let query = new URLSearchParams(window.location.search) 
 let movieID = query.get('movieID') 
 console.log(movieID)

 const [movie, setMovie] = useState(null)

 useEffect(() => { 
   const endpoint = `https://api.themoviedb.org/3/movie/${movieID}?api_key=c5256bd6913284c73d535a2dd158d799&language=es-ES`
   axios 
    .get(endpoint) 
    .then(res => { 
      console.log(res.data) 
      const movieData = res.data 
      setMovie(movieData) 
      }) 
      .catch(e => { 
        swal('hubo errores al intentar encontrar los datos de la pelicula') 
      })
    }, [setMovie]) 

    return ( 

      <> 
      { !token && <Redirect to='/' /> } 
      { !movie && <h3>por favor intente más tarde</h3> } 
      { movie && 

      <> 
      <h2>Titulo: {movie.title}</h2> 
      <div className="row"> 
      <div className='col-4'> 
      <img src= {`https://image.tmdb.org/t/p/w500/${movie.poster_path}`} className="card-img-top" alt="Card image cap"/> 
      </div> 
      <div className="col-8"> 
      <h5>Fecha de estreno: {movie.release_date}</h5> 
      <h5>Reseña: {movie.overview.substring(0,65)}...</h5> 
      <h5>Géneros:</h5> 
      <ul> 
        { movie.genres.map(oneGenre => <li>{oneGenre.name}</li>) } 
      </ul> 
      </div> 
      </div> 
      </> 
      } 
      </> 
      ) }

  export default Detalle;
