import { useEffect, useState } from 'react';
import { Redirect, Link } from 'react-router-dom';
import axios from 'axios';

function Detalle() {
    //<h2>podes ver el detalle de la peli porque tenes el token y la ruta esta protegida!</h2>

    const token = sessionStorage.getItem('token');

    let query = new URLSearchParams(window.location.search)

    let movieID = query.get('movieID');

    //let api_key = "c5256bd6913284c73d535a2dd158d799"

    const [ movie, setMovie ] = useState(null);

    useEffect(() => {
        const endpoint = `https://api.themoviedb.org/3/movie/${movieID}?api_key=c5256bd6913284c73d535a2dd158d799&language=es-ES`
        axios
            .get(endpoint)
            .then(res => {
                const movieData = res.data;
                setMovie(movieData);
            })
    }, [setMovie])


    return (
        <>
        { !token && <Redirect to="/" />}
        { movie && 
        <>
            <h2>Titulo: {movie.original_title}</h2>
            <div className="row">
            <div className="col-4">
            <img className="card-img-top" src={`https://image.tmdb.org/t/p/w500/${movie.poster_path}`} alt="Card image cap"/>
            </div>
            <div className="col-8">
                <h5>Fecha de estreno: {movie.release_date}</h5>
                <h5>Reseña:</h5>
                <p>{movie.tagline}</p>
                <h5>Géneros:</h5>
                <ul>
                    {movie.genres.map(genero => <li key={genero.id}> {genero.name}</li>)}
                </ul>
            </div>
        </div>
        </>
        }
        </>
    )
}

export default Detalle;
